import { useState, useEffect, useRef } from "react";
import { Helmet } from "react-helmet";
import { Table, DataBox, SearchBox, DeleteBox } from "../../components";

//Hooks
import useDietsHook from "./hooks/index";

//Styles
import "./style.scss";

const Questions = () => {
  const {
    getQuestions,
    deleteQuestion,
  } = useDietsHook();

  const [questions, setQuestions] = useState([]);

  const [deleteBoxVisible, setDeleteBoxVisible] = useState(false);
  const [answersBoxVisible, setAnswersBoxVisible] = useState(false)

  const [questionObj, setdietObj] = useState({
    _id: 0,
    question: "",
    time: "",
  });



  useEffect(() => {
    (async () => {
      const result = await getQuestions();

      if (result) {
        setQuestions(result);
      }
    })();
  }, []);



  const onClickDelete = (_id) => {
    setdietObj(questions.find((u) => u._id === _id));
    setDeleteBoxVisible(true);
  };

  return (
    <>
      <Helmet>
        <title>لوحة التحكم/ الاسئله</title>
      </Helmet>
      <DeleteBox
        visible={deleteBoxVisible}
        setVisible={setDeleteBoxVisible}
        title={`حذف السؤال رقم ${questionObj._id}`}
        onDelete={async () => {
        
          if (await deleteQuestion(questionObj._id)) {
            setQuestions(questions.filter((u) => u._id !== questionObj._id));
            setDeleteBoxVisible(false);
          }
        }}
      />


<DataBox
        visible={answersBoxVisible}
        setVisible={setAnswersBoxVisible}
        options={{
          name: "اضافة مقال جديد",
          onSave: async () => {
            const protein = await addProtein(addFormRef);
            if (protein) {
              setProteins([...proteins, protein]);
              setAddBoxVisible(false);
            }
          },
          btnSave: "اضافة",
          formRef: addFormRef,
        }}
        inputs={[
        
        ]}
      />
    
      <div className="main-container">
        <div className="page-position">
          <h2>لوحة التحكم</h2>
          <p>/</p>
          <h6>  الاسئله</h6>
        </div>
        <div className="container">
          {/*<SearchBox />*/}
          <Table
            actions={{
              delete: onClickDelete,
            }}
            headers={[
              "#",
              " السؤال",                    
              "تاريخ الإضافة",
            ]}
            data={
              questions &&
              questions.map((u) => [
                u._id,
                u.question,
                u.time,
              ])
            }
          />
        </div>
      </div>
    </>
  );
};

export default Questions;
